const serverData = (data) => ({
    hydrateTo: () => data,
    map: (fn) => serverData(fn(data)),
});
const hydratedData = () => ({
    hydrateTo: (hydratedData) => hydratedData,
    map: () => hydratedData(),
});
const from = (data, hydrated) => hydrated ? hydratedData() : serverData(data);
export const hydratable = {
    serverData,
    hydratedData,
    from,
};
